import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import RecipeList from './RecipeList';
import RecipeForm from './RecipeForm';
import RecipeDetail from './RecipeDetail'; // Importer le composant RecipeDetail

const App = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const storedRecipes = localStorage.getItem('recipes');
    if (storedRecipes) {
      setRecipes(JSON.parse(storedRecipes));
    }
  }, []);

  useEffect(() => {
    if (recipes.length > 0) {
      localStorage.setItem('recipes', JSON.stringify(recipes));
    }
  }, [recipes]);

  const addRecipe = (newRecipe) => {
    const updatedRecipes = [...recipes, { id: Date.now(), ...newRecipe }];
    setRecipes(updatedRecipes);
  };

  const deleteRecipe = (id) => {
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== id);
    setRecipes(updatedRecipes);
  };

  const editRecipe = (id, updatedRecipe) => {
    const updatedRecipes = recipes.map((recipe) =>
      recipe.id === id ? { ...recipe, ...updatedRecipe } : recipe
    );
    setRecipes(updatedRecipes);
  };

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={<RecipeList recipes={recipes} onDelete={deleteRecipe} />}
        />
        <Route
          path="/ajouter"
          element={<RecipeForm onAdd={addRecipe} recipes={recipes} />}
        />
        <Route
          path="/modifier/:id"
          element={<RecipeForm onEdit={editRecipe} recipes={recipes} />}
        />
        <Route
          path="/recipe/:id"
          element={<RecipeDetail recipes={recipes} />}
        />

      </Routes>
    </Router>
  );
};

export default App;
