import React from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/RecipeDetail.css';

const RecipeDetail = ({ recipes, onDelete }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const recipe = recipes.find((r) => r.id === parseInt(id));

  if (!recipe) {
    return <div>Recette introuvable</div>;
  }

  const handleEdit = () => {
    navigate(`/modifier/${recipe.id}`);
  };

  const updateLocalStorage = (updatedRecipes) => {
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
  };

  const handleLocalDelete = (id) => {
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== id);
    updateLocalStorage(updatedRecipes);
    onDelete(id);
    navigate('/');
  };

  return (
    <div className="recipe-detail">
      {recipe.image && (
        <img
          src={recipe.image}
          alt={recipe.title}
          className="recipe-card-image"
        />
      )}

      <h1 className='titlecard'>{recipe.title}</h1>
      <h3>Ingr√©dients :</h3>
      <ul>
        {recipe.ingredients?.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>

      <h3>√âtapes :</h3>
      <ol>
        {recipe.steps?.map((step, index) => (
          <li key={index}>{step}</li>
        ))}
      </ol>

      <h3>‚è±Ô∏è Temps de pr√©paration : {recipe.prepTime} min</h3>
      <h3>üç≥ Temps de cuisson : {recipe.cookTime} min</h3>
      <h3>üéØ Difficult√© : {recipe.difficulty}</h3>
      <h3>‚≠ê Note : {recipe.rating}/5</h3>

      <div className="recipe-actions">
        <button onClick={handleEdit} className="edit-button">Modifier</button>
        <button onClick={() => handleLocalDelete(recipe.id)} className="delete-button">Supprimer</button>
      </div>
    </div>
  );
};

export default RecipeDetail;
