import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/RecipeForm.css';

const categories = ["Petit-d√©jeuner", "D√©jeuner", "D√Æner", "Dessert", "Snacks"];

const RecipeForm = ({ onAdd, onEdit, recipes }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [recipeData, setRecipeData] = useState({
  title: '',
  image: null,
  ingredients: [''],
  steps: [''],
  category: '',
  prepTime: '',
  cookTime: '',
  difficulty: 'Facile',
  rating: 0,
});

  useEffect(() => {
    if (id) {
      const existingRecipe = recipes.find((r) => r.id === parseInt(id));
      if (existingRecipe) {
        setRecipeData({
          ...existingRecipe,
          ingredients: existingRecipe.ingredients || [''],
          steps: existingRecipe.steps || [''],
          image: existingRecipe.image || null,
          category: existingRecipe.category || '',  // Populate category if it exists
        });
      }
    }
  }, [id, recipes]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipeData({ ...recipeData, [name]: value });
  };

  const handleArrayChange = (e, index, field) => {
    const updatedArray = [...recipeData[field]];
    updatedArray[index] = e.target.value;
    setRecipeData({ ...recipeData, [field]: updatedArray });
  };

  const handleAddField = (field) => {
    setRecipeData({ ...recipeData, [field]: [...recipeData[field], ''] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (id) {
      onEdit(parseInt(id), recipeData);
    } else {
      onAdd(recipeData);
    }
    navigate('/');
  };

  // üéØ Gestion des changements pour les fichiers (image)
const handleImageChange = (e) => {
  const file = e.target.files[0];
  const reader = new FileReader();

  reader.onloadend = () => {
    setRecipeData({ ...recipeData, image: reader.result }); // Stocker l'image en base64
  };

  if (file) {
    reader.readAsDataURL(file);
  }
};


  return (
    <form onSubmit={handleSubmit} className="recipe-form">
      <h2>{id ? 'Modifier la recette' : 'Ajouter une recette'}</h2>

      <label>Image :</label>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />

        {/* üì∏ Aper√ßu de l'image si ajout√©e */}
        {recipeData.image && (
          <img
            src={recipeData.image}
            alt="Aper√ßu"
            style={{ width: "200px", marginTop: "10px", borderRadius: "8px" }}
          />
        )}

      <label>Titre :</label>
      <input
        type="text"
        name="title"
        value={recipeData.title}
        onChange={handleChange}
        required
      />

      <label>Ingr√©dients :</label>
      {recipeData.ingredients.map((ingredient, index) => (
        <input
          key={index}
          type="text"
          value={ingredient}
          onChange={(e) => handleArrayChange(e, index, 'ingredients')}
        />
      ))}
      <button type="button" onClick={() => handleAddField('ingredients')}>
        Ajouter un ingr√©dient
      </button>

      <label>√âtapes :</label>
      {recipeData.steps.map((step, index) => (
        <textarea
          key={index}
          value={step}
          onChange={(e) => handleArrayChange(e, index, 'steps')}
        />
      ))}
      <button type="button" onClick={() => handleAddField('steps')}>
        Ajouter une √©tape
      </button>

      <label>Temps de pr√©paration (en minutes) :</label>
        <input
          type="number"
          name="prepTime"
          value={recipeData.prepTime}
          onChange={handleChange}
          min="0"
          required
        />

        <label>Temps de cuisson (en minutes) :</label>
        <input
          type="number"
          name="cookTime"
          value={recipeData.cookTime}
          onChange={handleChange}
          min="0"
          required
        />

        <label>Difficult√© :</label>
        <select
          name="difficulty"
          value={recipeData.difficulty}
          onChange={handleChange}
          required
        >
          <option value="Facile">Facile</option>
          <option value="Moyenne">Moyenne</option>
          <option value="Difficile">Difficile</option>
        </select>

        <label>Note (1 √† 5) :</label>
        <input
          type="number"
          name="rating"
          value={recipeData.rating}
          onChange={handleChange}
          min="1"
          max="5"
          required
        />

      <label>Cat√©gorie :</label>
      <select
        name="category"
        value={recipeData.category}
        onChange={handleChange}
        required
      >
        <option value="">S√©lectionner une cat√©gorie</option>
        {categories.map((category, index) => (
          <option key={index} value={category}>
            {category}
          </option>
        ))}
      </select>

      <button type="submit">{id ? 'Modifier la recette' : 'Ajouter la recette'}</button>
    </form>
  );
};

export default RecipeForm;
