import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const RecipeForm = () => {
  const { id } = useParams(); // Récupère l'ID de la recette depuis l'URL
  const [recipe, setRecipe] = useState(null);
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [steps, setSteps] = useState('');

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await fetch(`http://localhost:8000/recipe?id=${id}`);
        if (!response.ok) {
          throw new Error(`Erreur serveur: ${response.status}`);
        }
        const data = await response.json();
        setRecipe(data);
        setTitle(data.title);
        setIngredients(data.ingredients.join(', ')); // Mettre à jour les états
        setSteps(data.steps.join(', '));
      } catch (error) {
        console.error("Erreur de chargement:", error);
      }
    };
    fetchRecipe();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedRecipe = { title, ingredients: ingredients.split(', '), steps: steps.split(', ') };
    const response = await fetch(`http://localhost:8000/recipe/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedRecipe),
    });
    if (response.ok) {
      console.log("Recette mise à jour");
      // Rediriger vers le détail de la recette après mise à jour
    } else {
      console.error("Erreur lors de la mise à jour de la recette");
    }
  };

  if (!recipe) return <p>Chargement...</p>;

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Titre :
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
      </label>
      <label>
        Ingrédients :
        <input type="text" value={ingredients} onChange={(e) => setIngredients(e.target.value)} />
      </label>
      <label>
        Étapes :
        <input type="text" value={steps} onChange={(e) => setSteps(e.target.value)} />
      </label>
      <button type="submit">Sauvegarder</button>
    </form>
  );
};

export default RecipeForm;
