import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // To get the recipe id from the URL
import axios from 'axios';
import '../styles/RecipeDetail.css';

const RecipeDetail = () => {
  const { id } = useParams(); // Get the recipe ID from the URL
  const [recipe, setRecipe] = useState(null); // State to store the fetched recipe
  const [loading, setLoading] = useState(true); // State to handle loading

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/recipe?id=${id}`);
        setRecipe(response.data); // Set the fetched recipe data
        setLoading(false); // Stop loading
      } catch (error) {
        console.error('Error fetching recipe:', error);
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]); // The effect runs when the component mounts or when 'id' changes

  if (loading) {
    return <div>Loading...</div>; // Show a loading message while fetching data
  }

  if (!recipe) {
    return <div>Recipe not found</div>; // If the recipe is not found, display an error message
  }

  return (
    <div className="recipe-detail">
      <h1>{recipe.title}</h1>
      {recipe.image && <img src={recipe.image} alt={recipe.title} className="recipe-detail-image" />}
      <h3>Ingredients</h3>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>
      <h3>Steps</h3>
      <ol>
        {recipe.steps.map((step, index) => (
          <li key={index}>{step}</li>
        ))}
      </ol>
      <p><strong>Preparation Time:</strong> {recipe.prepTime} minutes</p>
      <p><strong>Cooking Time:</strong> {recipe.cookTime} minutes</p>
      <p><strong>Difficulty:</strong> {recipe.difficulty}</p>
      <p><strong>Rating:</strong> {recipe.rating}</p>
    </div>
  );
};

export default RecipeDetail;
