import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import '../styles/RecipeDetail.css';

const RecipeForm = () => {
  const { id } = useParams(); // Récupérer l'ID si on est en modification
  const navigate = useNavigate();

  // État pour stocker la recette
  const [recipe, setRecipe] = useState({
    title: '',
    ingredients: '',
    steps: '',
    prepTime: '',
    difficulty: '',
    rating: '',
    image: ''
  });

  // Charger une recette existante si `id` est présent
  useEffect(() => {
    if (id) {
      fetch(`http://localhost:8000/recipe/${id}`)
        .then(response => response.json())
        .then(data => setRecipe(data))
        .catch(error => console.error("Erreur de chargement:", error));
    }
  }, [id]);

  // Fonction pour gérer les changements dans le formulaire
  const handleChange = (e) => {
    setRecipe({ ...recipe, [e.target.name]: e.target.value });
  };

  // Fonction pour soumettre le formulaire (POST pour créer, PUT pour modifier)
  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = id ? "PUT" : "POST";
    const url = id ? `http://localhost:8000/recipe/${id}` : "http://localhost:8000/recipe";

    try {
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(recipe)
      });

      if (!response.ok) {
        throw new Error(`Erreur serveur: ${response.status}`);
      }

      navigate('/'); // Redirection vers la liste des recettes
    } catch (error) {
      console.error("Erreur lors de l'envoi :", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Titre :
        <input type="text" name="title" value={recipe.title} onChange={handleChange} required />
      </label>
      <label>
        Category :
        <input type="text" name="title" value={recipe.category} onChange={handleChange} required />
      </label>
      <label>
        Ingrédients (séparés par des virgules) :
        <input type="text" name="ingredients" value={recipe.ingredients} onChange={handleChange} required />
      </label>
      <label>
        Étapes (séparées par des virgules) :
        <input type="text" name="steps" value={recipe.steps} onChange={handleChange} required />
      </label>
      <label>
        Temps de préparation :
        <input type="text" name="prepTime" value={recipe.prepTime} onChange={handleChange} />
      </label>
      <label>
        Difficulté :
        <select name="difficulty" value={recipe.difficulty} onChange={handleChange}>
          <option value="Facile">Facile</option>
          <option value="Moyen">Moyen</option>
          <option value="Difficile">Difficile</option>
        </select>
      </label>
      <label>
        Note :
        <input type="number" name="rating" value={recipe.rating} onChange={handleChange} min="1" max="5" />
      </label>
      <label>
        URL de l’image :
        <input type="text" name="image" value={recipe.image} onChange={handleChange} />
      </label>
      <button type="submit">{id ? "Modifier" : "Ajouter"} la recette</button>
    </form>
  );
};

export default RecipeForm;
