import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const RecipeDetail = () => {
  const { id } = useParams(); // Récupère l'ID depuis l'URL
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const url = `http://localhost:8000/recipe?id=${id}`;

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Erreur serveur: ${response.status}`);
        }

        const data = await response.json();
        setRecipe(data);
      } catch (error) {
        console.error("Erreur de chargement:", error);
      }
    };

    fetchRecipe();
  }, [id]);

  // Fonction pour mettre à jour la recette
  const handleEdit = async (updatedData) => {
    const patchUrl = `http://localhost:8000/recipe/${id}`;
    try {
      const response = await fetch(patchUrl, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      if (!response.ok) {
        throw new Error(`Erreur de mise à jour: ${response.status}`);
      }

      const updatedRecipe = await response.json();
      setRecipe(updatedRecipe); // Mettre à jour les données de la recette dans l'état
      console.log('Recette mise à jour:', updatedRecipe);
    } catch (error) {
      console.error("Erreur lors de la mise à jour:", error);
    }
  };

  // Fonction pour supprimer la recette
  const handleDelete = async () => {
    const deleteUrl = `http://localhost:8000/recipe/${id}`;
    try {
      const response = await fetch(deleteUrl, { method: 'DELETE' });
      if (!response.ok) {
        throw new Error(`Erreur de suppression: ${response.status}`);
      }
      // Rediriger vers la liste des recettes après suppression
      navigate('/');
    } catch (error) {
      console.error("Erreur lors de la suppression:", error);
    }
  };

  if (!recipe) return <p>Chargement...</p>;

  return (
    <div>
      <h2>{recipe.title}</h2>
      {recipe.image && <img src={recipe.image} alt={recipe.title} />}
      <p><strong>Ingrédients:</strong> {recipe.ingredients.join(', ')}</p>
      <p><strong>Étapes:</strong> {recipe.steps.join(', ')}</p>
      <p><strong>Temps de préparation:</strong> {recipe.prepTime}</p>
      <p><strong>Difficulté:</strong> {recipe.difficulty}</p>
      <p><strong>Note:</strong> {recipe.rating}</p>

      {/* Boutons Edit et Delete */}
      <button onClick={() => handleEdit({ title: 'Nouveau Titre' })}>Editer</button>
      <button onClick={handleDelete}>Supprimer</button>

      <button onClick={() => navigate('/')}>Retour</button>
    </div>
  );
};

export default RecipeDetail;
