import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const navigate = useNavigate();

  return (
    <div className="recipe-card" onClick={() => navigate(`/recipe/${recipe._id}`)}>
      {recipe.image && <img src={recipe.image} alt={recipe.title} className="recipe-card-image" />}
      <h3>{recipe.title}</h3>
      <p><strong>Ingrédients:</strong> {recipe.ingredients.join(', ')}</p>
      <p><strong>Étapes:</strong> {recipe.steps.join(', ')}</p>
      <p><strong>Temps de préparation:</strong> {recipe.prepTime}</p>
      <p><strong>Difficulté:</strong> {recipe.difficulty}</p>
      <p><strong>Note:</strong> {recipe.rating}</p>
    </div>
  );
};

export default RecipeCard;
