import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/RecipeDetail.css';

const RecipeDetail = ({ onDelete }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Charger la recette depuis l'API
  useEffect(() => {
    axios.get(`http://localhost:8000/recipe/${id}`)
      .then((response) => {
        setRecipe(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Erreur de r√©cup√©ration de la recette :", error);
        setError("Recette introuvable");
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>{error}</div>;


  return (
    <div className="recipe-detail">
      <h1 className="titlecard">{recipe.name}</h1>

      <h3>Ingr√©dients :</h3>
      <ul>{recipe.ingredients?.map((ingredient, index) => <li key={index}>{ingredient}</li>)}</ul>

      <h3>√âtapes :</h3>
      <ol>{recipe.steps?.map((step, index) => <li key={index}>{step}</li>)}</ol>

      <h3>‚è±Ô∏è Temps de pr√©paration : {recipe.prepTime} min</h3>
      <h3>üç≥ Temps de cuisson : {recipe.cookTime} min</h3>
      <h3>üéØ Difficult√© : {recipe.difficulty}</h3>
      <h3>‚≠ê Note : {recipe.rating}/5</h3>
    </div>
  );
};

export default RecipeDetail;
