import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const RecipeDetail = () => {
  const { id } = useParams(); // Récupère l'ID depuis l'URL
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    ingredients: '',
    steps: '',
    prepTime: '',
    difficulty: '',
    rating: ''
  });

  const url = `http://localhost:8000/recipe?id=${id}`;

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Erreur serveur: ${response.status}`);
        }

        const data = await response.json();
        setRecipe(data);
        setFormData({
          title: data.title,
          ingredients: data.ingredients.join(', '),
          steps: data.steps.join(', '),
          prepTime: data.prepTime,
          difficulty: data.difficulty,
          rating: data.rating
        });
      } catch (error) {
        console.error("Erreur de chargement:", error);
      }
    };

    fetchRecipe();
  }, [id]);

  // Fonction pour supprimer la recette
  const handleDelete = async () => {
    const deleteUrl = `http://localhost:8000/recipe/${id}`;
    try {
      const response = await fetch(deleteUrl, { method: 'DELETE' });
      if (!response.ok) {
        throw new Error(`Erreur de suppression: ${response.status}`);
      }
      // Rediriger vers la liste des recettes après suppression
      navigate('/');
    } catch (error) {
      console.error("Erreur lors de la suppression:", error);
    }
  };

  // Fonction pour gérer la mise à jour
  const handleUpdate = async (e) => {
    e.preventDefault();

    const updatedData = {
      title: formData.title,
      ingredients: formData.ingredients.split(','),
      steps: formData.steps.split(','),
      prepTime: formData.prepTime,
      difficulty: formData.difficulty,
      rating: formData.rating
    };

    try {
      const response = await fetch(`http://localhost:8000/recipe/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      if (!response.ok) {
        throw new Error(`Erreur de mise à jour: ${response.status}`);
      }

      const updatedRecipe = await response.json();
      setRecipe(updatedRecipe);
      alert("Recette mise à jour avec succès !");
    } catch (error) {
      console.error("Erreur lors de la mise à jour:", error);
    }
  };

  if (!recipe) return <p>Chargement...</p>;

  return (
    <div>
      <h2>{recipe.title}</h2>
      {recipe.image && <img src={recipe.image} alt={recipe.title} />}

      <form onSubmit={handleUpdate}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
          />
        </div>
        <div>
          <label>Ingrédients:</label>
          <input
            type="text"
            value={formData.ingredients}
            onChange={(e) => setFormData({ ...formData, ingredients: e.target.value })}
          />
        </div>
        <div>
          <label>Étapes:</label>
          <input
            type="text"
            value={formData.steps}
            onChange={(e) => setFormData({ ...formData, steps: e.target.value })}
          />
        </div>
        <div>
          <label>Temps de préparation:</label>
          <input
            type="text"
            value={formData.prepTime}
            onChange={(e) => setFormData({ ...formData, prepTime: e.target.value })}
          />
        </div>
        <div>
          <label>Difficulté:</label>
          <input
            type="text"
            value={formData.difficulty}
            onChange={(e) => setFormData({ ...formData, difficulty: e.target.value })}
          />
        </div>
        <div>
          <label>Note:</label>
          <input
            type="number"
            value={formData.rating}
            onChange={(e) => setFormData({ ...formData, rating: e.target.value })}
          />
        </div>

        <button type="submit">Mettre à jour</button>
      </form>

      <button onClick={handleDelete}>Supprimer</button>

      <button onClick={() => navigate('/')}>Retour</button>
    </div>
  );
};

export default RecipeDetail;
